mapper.insert:
insert into seven_message (id, m_type, m_name
  )
values (#{id,jdbcType=INTEGER}, #{mType,jdbcType=INTEGER}, #{mName,jdbcType=VARCHAR}
  )

mapper.update:
update seven_message
set m_type = #{mType,jdbcType=INTEGER},
  m_name = #{mName,jdbcType=VARCHAR}
where id = #{id,jdbcType=INTEGER}

mapper.select:
select id, m_type, m_name
from seven_message
where id = #{id,jdbcType=INTEGER}

select id, m_type, m_name
from seven_message


		m_name like #{mName}


		and m_type = #{mType}
