mapper.update:
update
seven_staff
set s_state = #{sState}
where id =
#{id}

mapper.select:
select id, s_name, s_sex, s_date_birth, s_phone, s_email, s_address,
s_state
from seven_staff


		s_sex=#{sSex}


		and s_name like #{sName}


		and s_date_birth like #{sDateBirth}

select id, s_name, s_phone, s_email
from seven_staff


		and s_name like #{sName}
